import { useAuth } from './useAuth';

interface ProStatus {
  isPro: boolean;
  isLoading: boolean;
  redirectToCheckout: () => void;
}

const FREE_TIER_SEMESTER_LIMIT = 2;
const LEMON_SQUEEZY_CHECKOUT_URL = 'https://gradegoal.lemonsqueezy.com/buy/1a922e3f-709c-47a3-9395-7b93865cad80';

export function useProStatus(): ProStatus {
  const { user, isLoading: authLoading } = useAuth();

  const isPro = user?.subscriptionTier === 'pro';

  const redirectToCheckout = () => {
    if (!user?.id) {
      console.error('User must be logged in to purchase');
      return;
    }

    const checkoutUrl = `${LEMON_SQUEEZY_CHECKOUT_URL}?checkout[custom][user_id]=${encodeURIComponent(user.id)}`;
    window.location.href = checkoutUrl;
  };

  return {
    isPro,
    isLoading: authLoading,
    redirectToCheckout,
  };
}

export function canCreateSemester(semesterCount: number, isPro: boolean): boolean {
  if (isPro) return true;
  return semesterCount < FREE_TIER_SEMESTER_LIMIT;
}

export { FREE_TIER_SEMESTER_LIMIT };

server/lemonSqueezyWebhook.ts.content:
import type { Request, Response } from 'express';
import crypto from 'crypto';
import { storage } from './storage';

interface LemonSqueezyWebhookPayload {
  meta: {
    event_name: string;
    custom_data?: {
      user_id?: string;
    };
  };
  data: {
    id: string;
    type: string;
    attributes: {
      status: string;
      first_order_item?: {
        product_id: number;
        product_name: string;
      };
    };
  };
}

function verifyWebhookSignature(rawBody: Buffer | string, signature: string, secret: string): boolean {
  const hmac = crypto.createHmac('sha256', secret);
  const digest = hmac.update(rawBody).digest('hex');

  try {
    return crypto.timingSafeEqual(Buffer.from(signature), Buffer.from(digest));
  } catch {
    return false;
  }
}

export async function handleLemonSqueezyWebhook(req: Request, res: Response): Promise<void> {
  const secret = process.env.LEMONSQUEEZY_WEBHOOK_SECRET;

  if (!secret) {
    console.error('LEMONSQUEEZY_WEBHOOK_SECRET is not configured');
    res.status(500).json({ error: 'Webhook secret not configured' });
    return;
  }

  const signature = req.headers['x-signature'] as string;

  if (!signature) {
    console.error('Missing webhook signature');
    res.status(401).json({ error: 'Missing signature' });
    return;
  }

  const rawBody = req.rawBody as Buffer | undefined;

  if (!rawBody) {
    console.error('Missing raw body for signature verification');
    res.status(500).json({ error: 'Request processing error' });
    return;
  }

  if (!verifyWebhookSignature(rawBody, signature, secret)) {
    console.error('Invalid webhook signature');
    res.status(401).json({ error: 'Invalid signature' });
    return;
  }

  const payload = req.body as LemonSqueezyWebhookPayload;
  const eventName = payload.meta?.event_name;

  console.log(`Received Lemon Squeezy webhook: ${eventName}`);

  try {
    if (eventName === 'order_created') {
      const userId = payload.meta?.custom_data?.user_id;

      if (!userId) {
        console.error('No user_id found in webhook custom data');
        res.status(400).json({ error: 'Missing user_id' });
        return;
      }

      console.log(`Upgrading user ${userId} to pro tier`);
      await storage.updateUserStripeInfo(userId, { subscriptionTier: 'pro' });

      res.status(200).json({ success: true, message: 'User upgraded to pro' });
      return;
    }

    res.status(200).json({ success: true, message: 'Event acknowledged' });
  } catch (error) {
    console.error('Error processing Lemon Squeezy webhook:', error);
    res.status(500).json({ error: 'Internal server error' });
  }
}

client/src/components/PaywallModal.tsx.content:
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogDescription } from '@/components/ui/dialog';
import { Button } from '@/components/ui/button';
import { Crown, Check, Loader2 } from 'lucide-react';
import { useProStatus, FREE_TIER_SEMESTER_LIMIT } from '@/hooks/useProStatus';

interface PaywallModalProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
  trigger?: 'semester_limit' | 'feature' | 'manual';
}

const PRO_FEATURES = [
  'סמסטרים ללא הגבלה',
  'קורסים ללא הגבלה',
  'סימולציות What-If מתקדמות',
  'ייצוא דוחות PDF',
  'דירוג אחוזונים',
  'אנליטיקות מתקדמות',
  'ללא פרסומות',
];

export function PaywallModal({ open, onOpenChange, trigger = 'manual' }: PaywallModalProps) {
  const { redirectToCheckout, isLoading } = useProStatus();

  const handlePurchase = () => {
    redirectToCheckout();
  };

  const getTriggerMessage = () => {
    switch (trigger) {
      case 'semester_limit':
        return `הגעתם למגבלת ${FREE_TIER_SEMESTER_LIMIT} הסמסטרים בחינם. שדרגו ל-Pro כדי להוסיף עוד!`;
      case 'feature':
        return 'פיצ\'ר זה זמין למנויי Pro. שדרגו עכשיו!';
      default:
        return 'שדרגו לחוויה המלאה';
    }
  };

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="max-w-md max-h-[90vh] overflow-y-auto" dir="rtl">
        <DialogHeader className="text-center">
          <div className="mx-auto mb-4 flex items-center justify-center w-16 h-16 rounded-full bg-primary/10">
            <Crown className="w-8 h-8 text-primary" />
          </div>
          <DialogTitle className="text-2xl font-bold">
            GradeGoal Pro
          </DialogTitle>
          <DialogDescription className="text-base mt-2">
            {getTriggerMessage()}
          </DialogDescription>
        </DialogHeader>

        <div className="space-y-4 mt-6">
          <div className="space-y-3">
            {PRO_FEATURES.map((feature, index) => (
              <div 
                key={index} 
                className="flex items-center gap-3"
                data-testid={`text-feature-${index}`}
              >
                <div className="flex-shrink-0 w-5 h-5 rounded-full bg-primary/10 flex items-center justify-center">
                  <Check className="w-3 h-3 text-primary" />
                </div>
                <span className="text-sm">{feature}</span>
              </div>
            ))}
          </div>

          {isLoading ? (
            <div className="flex items-center justify-center py-8">
              <Loader2 className="w-6 h-6 animate-spin text-muted-foreground" />
            </div>
          ) : (
            <Button 
              className="w-full mt-6" 
              size="lg"
              onClick={handlePurchase}
              data-testid="button-buy-pro"
            >
              <Crown className="w-4 h-4 me-2" />
              שדרגו ל-Pro
            </Button>
          )}

          <p className="text-xs text-muted-foreground text-center mt-4">
            ניתן לבטל בכל עת. התשלום מתבצע באופן מאובטח.
          </p>
        </div>

        <Button 
          variant="ghost" 
          className="mt-4 w-full"
          onClick={() => onOpenChange(false)}
          data-testid="button-close-paywall"
        >
          אולי אחר כך
        </Button>
      </DialogContent>
    </Dialog>
  );
}

export default PaywallModal;

